<!DOCTYPE html><html><head>    <meta charset="UTF-8">    <title>后台管理系统</title></head><style type="text/css">.wrapper {width: 1000px;margin: 20px auto;}h2 {text-align: center;}.add {margin-bottom: 20px;}.add a {text-decoration: none;color: #fff;background-color: green;padding: 6px;border-radius: 5px;}td {text-align: center;}</style><body>    <div class="wrapper">        <h2>后台管理系统</h2>        <div class="add">        &emsp;&emsp;&emsp;<?php     header('Content-type:text/html; charset=utf-8');    // 开启Session    session_start();     // 首先判断Cookie是否有记住了用户信息    if (isset($_COOKIE['username'])) {        # 若记住了用户信息,则直接传给Session        $_SESSION['username'] = $_COOKIE['username'];        $_SESSION['islogin'] = 1;    }    if (isset($_SESSION['islogin'])) {        // 若已经登录        echo "<a href='login/logout.php'>注销</a>";    } else {        // 若没有登录        header('location:login/login.html');    } ?>        </div>        <table width="960" border="1">            <tr>                <th>ID</th>                <th>key</th>                <th>时间</th>            </tr>             <?php                // 1.导入配置文件                require "dbconfig.php";                // 2. 连接mysql                $link = @mysql_connect(HOST,USER,PASS) or die("提示：数据库连接失败！");                // 选择数据库                mysql_select_db(DBNAME,$link);                // 编码设置                mysql_set_charset('utf8',$link);                // 3. 从DBNAME中查询到news数据库，返回数据库结果集,并按照addtime降序排列                $sql = 'select * from d1 order by id desc';                // 结果集                $result = mysql_query($sql,$link);                // var_dump($result);die;                 // 解析结果集,$row为新闻所有数据，$newsNum为新闻数目                $newsNum=mysql_num_rows($result);               for($i=0; $i<$newsNum; $i++){                    $row = mysql_fetch_assoc($result);                    echo "<tr>";                    echo "<td>{$row['id']}</td>";                    echo "<td>{$row['waxx']}</td>";                    echo "<td>{$row['time']}</td>";                    echo "</tr>";                }               // 5. 释放结果集                mysql_free_result($result);                mysql_close($link);         ?>        </table>    </div>         <script type="text/javascript">        function del () {         if (confirm("确定清空吗？")){             window.location = "del.php";            }        }   </script></body></html>